mapper.select:
SELECT p.uuid, p.ptype, p.pname, p.pcode, p.param, p.data_status as dataStatus, IFNULL( (SELECT u.ename FROM cle_uav u WHERE u.uuid = ep.equipment_id AND u.data_status = '1'), 'æ— ' ) AS ename FROM cle_parts p LEFT JOIN cle_equipment_parts ep ON p.uuid = ep.parts_id AND ep.data_status = '1'
left join cle_dept d on d.uuid = p.dept_id
left join cle_dept d2 on d2.uuid=#{p.deptId}
WHERE 1 = 1
AND d.dept_code like CONCAT(d2.dept_code,'%')
AND p.data_status = #{p.dataStatus}
AND p.uuid = #{p.uuid}
AND p.ptype in
#{type}
AND p.pname like CONCAT('%', #{p.pname}, '%')
AND p.pcode like CONCAT('%', #{p.pcode}, '%')
AND IF(ep.uuid, 1, 0) in
#{isMount}
AND ep.equipment_id = #{p.equipmentId}
OR (IFNULL(ep.uuid, 0) = 0 AND p.data_status = '1')
